function toN3(suri,property,value,lang) {
    if(value) {

        if(lang)
            return "<"+suri+"> <"+property+"> \""+value+"\"@"+lang+" .\n";
        else
            return "<"+suri+"> <"+property+"> \""+value+"\" .\n";
    }
    return "";
}
function toN3_property(suri,property1,property2) {
    return "<"+suri+"> <"+property1+"> <"+property2+"> .\n";
}

